package com.oasis.tmsv5.util.tools;import java.util.regex.Matcher;import java.util.regex.Pattern;public class JavaTypeUtil {    public static boolean isBool(String str) {        return (str.equalsIgnoreCase(Boolean.TRUE.toString())) || (str.equalsIgnoreCase(Boolean.FALSE.toString()));    }    public static boolean isInt(String str) {        Pattern pattern = Pattern.compile("^[0-9]+$");        Matcher match = pattern.matcher(str);        return match.matches();    }}