package com.oasis.tmsv5.util.tools;public class NaturalZoneUtil {    public static final String NAMEPATH_SEPERATOR = "\\|";    public static String getFullName(String namePath) {        String ret = "";        if (namePath != null && namePath.length() > 0) {            String[] pathSection = namePath.split(NaturalZoneUtil.NAMEPATH_SEPERATOR);            if (pathSection.length < 1) {                return "";            }            for (int i = 1; i < pathSection.length; i++) {                ret += pathSection[i];            }        }        return ret;    }}